def basement_character(puzzle_input):
    basement = -1
    current_floor = 0
    count = 0
    for c in puzzle_input:
        count += 1
        if c == '(':
            current_floor += 1
        elif c == ')':
            current_floor -= 1
        if current_floor == basement:
            return count


def floor_checker(puzzle_input):
    out = 0
    for c in puzzle_input:
        if c == '(':
            out += 1
        elif c == ')':
            out -= 1
    return out   

def wrapping_paper_surface_area(lwh):
    l = int(lwh[0])
    w = int(lwh[1])
    h = int(lwh[2])
    return 2*l*w + 2*w*h + 2*h*l + min([l*w, w*h, h*l])

def wrapping_paper():
    out = 0
    with open('wrappingpaper') as wp:
        lines = wp.readlines()
        for line in lines:
            line = line.strip()
            line = line.split('x')
            out += wrapping_paper_surface_area(line)
    return out


def ribbons(filename):
    out = 0
    with open(filename) as wp:
        for line in wp:
            l, w, h = line.split('x')
            l, w, h = int(l), int(w), int(h)
            ribbon = 2 * min(l+w, w+h, h+l)
            bow = l*w*h
            out += ribbon + bow
    return out

print('floor checker',floor_checker(puzzle_input))
print('basement character',basement_character(puzzle_input))
print('wrapping paper',wrapping_paper())
print('feet of ribon', ribbons('wrappingpaper'))


class TestFloorChecker(unittest.TestCase):
    def testUpOnce(self):
        test_input = '('
        self.assertEqual(floor_checker(test_input), 1)
    def testDownOnce(self):
        test_input = ')'
        self.assertEqual(floor_checker(test_input), -1)
    def testTwoUpTwoDown(self):
        test_input = '(())'
        self.assertEqual(floor_checker(test_input), 0)
        test_input = '()()'
        self.assertEqual(floor_checker(test_input), 0)
        
    def testThree(self):
        test_input = '((('
        self.assertEqual(floor_checker(test_input), 3)
        test_input = '(()(()('
        self.assertEqual(floor_checker(test_input), 3)
        test_input = '))((((('
        self.assertEqual(floor_checker(test_input), 3)

    def testNegativeOne(self):
        test_input = '())'
        self.assertEqual(floor_checker(test_input), -1)
        test_input = '))('
        self.assertEqual(floor_checker(test_input), -1)

    def testNegativeThree(self):
        test_input = ')))'
        self.assertEqual(floor_checker(test_input), -3)
        test_input = ')())())'
        self.assertEqual(floor_checker(test_input), -3)

    def testWrappingPaper(self):
        self.assertEqual(wrapping_paper_surface_area([2,3,4]), 58)
        self.assertEqual(wrapping_paper_surface_area([1,1,10]), 43)

    def testRibbon(self):
        self.assertEqual(ribbons('testwrappingpaper'), 34)
        print('ribbon in feet', ribbons('wrappingpaper'))

